# 随机数用于做延迟
def random_Delay():
    random_Delay = random.randint(random.randint(5, 8), random.randint(10, 15))
    print('正在随机延迟,防止访问频繁...等待', random_Delay, '秒')
    time.sleep(random_Delay)


def greedy():
    count_id = 1
    count_id2 = 1
    page = 0
    while True:
        # 请求search_company_for_lib获取密文数据  a是翻页参数  规律是 第一页0 第二页20-----------------------------------------------------------------
        def get_html1(a):
            item = execjs.compile(Path('man.js').read_text(encoding='utf-8')).call('item', int(a))
            json_data = {
                "payload": item['payload'],
                "sig": item['sig'],
                "v": item['v'],
            }
            response = requests.post(
                'https://www.xiniudata.com/api/search3/company/search_company_for_lib',
                headers=headers,
                json=json_data,
            ).json()
            # 延迟
            time.sleep(random.randint(4, 6))
            return response

        ## 临时数据temp1 不用管
        ## temp1 = {'code': 0, 'd': '', 'v': 1}
        ## 这是search_company_for_lib的密文数据 传入js得到list_companies4_list_by_codes请求参数
        item2 = execjs.compile(Path('man.js').read_text(encoding='utf-8')).call('o_map', get_html1(page))

        # # 请求list_companies4_list_by_codes获取密文数据--------------------------------------------------------------------------------------
        def get_html2(a):
            json_data = {
                "payload": a['payload'],
                "sig": a['sig'],
                "v": a['v'],
            }
            response = requests.post(
                'https://www.xiniudata.com/api2/service/x_service/person_company4_list/list_companies4_list_by_codes',
                headers=headers,
                json=json_data,
            ).json()
            return response

        ## 获取密文数据传入js解密  得到最终需要的数据
        ## temp2 = {'code': 0, 'd': '', 'v': 1}
        it_data = execjs.compile(Path('man.js').read_text(encoding='utf-8')).call('list_mi', get_html2(item2))
        for i in tqdm(it_data['list'], desc='正在全速下载中'):
            code = i['code']
            # 公司名字
            name = i['name']
            # 简介
            brief = i['brief']
            # 行业
            try:
                tagNameList = '-'.join(u for u in i['tagNameList'])
            except Exception:
                tagNameList = '无'
            # 国家
            regionName = i['regionName']
            # 省份
            provinceName = i['provinceName']
            # 市
            cityName = i['cityName']
            # 区
            districtName = i['districtName']
            # 合并地区
            locationName = f'{regionName}·{provinceName}|{cityName}|{districtName}'
            # 融资时间 这是时间戳 下面转为具体时间
            fundingDate = int(i['funding']['fundingDate'])
            # 将毫秒时间戳转换为秒时间戳
            timestamp = fundingDate / 1000

            # 使用 time.localtime() 将秒级时间戳转换为时间元组
            time_tuple = time.localtime(timestamp)

            # 使用 time.strftime() 格式化时间为 年-月-日   这就是融资具体时间
            formatted_date = time.strftime('%Y-%m-%d', time_tuple)
            # 当前轮次
            roundName = i['roundName']
            # fundingDesc包含融资金额等数据
            fundingDesc = json.loads(i['funding']['fundingDesc'])
            # 融资金额
            money = fundingDesc['money']
            # 投资方
            investorStr = fundingDesc['investorStr']
            if investorStr:
                investorStr = json.loads(investorStr)
                investorStr = ''.join([u['text'] for u in investorStr])
            else:
                investorStr = '未披露'
            ws.append([count_id, name, brief, tagNameList, locationName, formatted_date, roundName, money, investorStr])
            count_id += 1
            time.sleep(0.2)
        print('数据获取完毕,当前第', count_id2, '页')
        count_id2 += 1
        page += 20
        # 延迟
        random_Delay()


